def say_hello(name):
    print("Привет,", name)

def sum5(a,b):
    return a + b + 5

def find_discriminant(a,b,c):
    d = b ** 2 - 4 * a * c
    return d

def input_int(text):
    while True:
        try:
            num = int(input(text))
            return num
        except ValueError:
            print("Это не число")

def show_coolness(name, coolness=100):
    print("{0} - его крутость: {1}".format(name,coolness))


