def show_friends(*args):
    print("Мои друзья: ", end="")
    for arg in args:
        print(arg, end=", ")
    print()

show_friends("Арсен","Омар")
show_friends("Арсен","Омар","Гаджикурбан")
show_friends()

def show_friends2(*args,**kwargs):
    print("Мои друзья: ", end="")
    for arg in args:
        print(arg, end=", ")
    for kwarg in kwargs:
        print(kwargs[kwarg], end=", ")
    print()

show_friends2("Арсен","Омар",name="Гаджикурбан")
